INSERT INTO `bacaan` (`IDBacaan`, `judul`, `pengarang`, `isi`, `lexile`, `GFI`, `batassuku`, `stemming`, `tanggal`) VALUES
('BAC-0279', 'A PHONETIC CONTRASTIVE ANALYSIS OF ENGLISH WORDS IN THREE JAPANESE SONGS BY AKB48', 'Harwintha Y. Anjarningsih, Anisa Saraayu', 'Telah banyak penelitian yang mengungkapkan bagaimana penutur bahasa Jepang memiliki perbedaan dalam mengucapkan kata-kata berbahasa Inggris. Namun, tidak banyak penelitian yang menjelaskan penyebab perbedaan tersebut, terlebih lagi mengaitkan perbedaan tersebut dengan pengaruh bahasa ibu. Dengan melakukan perbandingan antara dua struktur bunyi bahasa menggunakan Contrastive Analysis Hypothesis (CAH) dapat dilihat bagaimana bahasa ibu mempengaruhi bahasa asing dan menyebabkan perbedaan pengujaran pada lagu-lagu populer. Hasil transkripsi dari 3 lagu milik AKB48, Heavy Rotation, Sugar Rush, dan Namida Surpise, dijadikan sumber data untuk melihat pengaruh bahasa ibu. Hasil penelitian menunjukkan penambahan bunyi vokal, perubahan jumlah suku kata, serta perubahan letak artikulasi vokal, penggantian bunyi konsonan dengan konsonan lain, dan penghilangan konsonan terjadi di ketiga lagu. Perubahan perubahan fonetik ini dapat berkontribusi kepada diskusi mengenai hubungan lirik dan melodi pada lagu-lagu yang menggunakan dua atau lebih bahasa (bilingual).', 1745, 22, 3, 'Tidak', '2016-07-20'),
('BAC-0280', 'A PHONETIC CONTRASTIVE ANALYSIS OF ENGLISH WORDS IN THREE JAPANESE SONGS BY AKB48', 'Harwintha Y. Anjarningsih, Anisa Saraayu', 'Telah banyak penelitian yang mengungkapkan bagaimana penutur bahasa Jepang memiliki perbedaan dalam mengucapkan kata-kata berbahasa Inggris. Namun, tidak banyak penelitian yang menjelaskan penyebab perbedaan tersebut, terlebih lagi mengaitkan perbedaan tersebut dengan pengaruh bahasa ibu. Dengan melakukan perbandingan antara dua struktur bunyi bahasa menggunakan Contrastive Analysis Hypothesis (CAH) dapat dilihat bagaimana bahasa ibu mempengaruhi bahasa asing dan menyebabkan perbedaan pengujaran pada lagu-lagu populer. Hasil transkripsi dari 3 lagu milik AKB48, Heavy Rotation, Sugar Rush, dan Namida Surpise, dijadikan sumber data untuk melihat pengaruh bahasa ibu. Hasil penelitian menunjukkan penambahan bunyi vokal, perubahan jumlah suku kata, serta perubahan letak artikulasi vokal, penggantian bunyi konsonan dengan konsonan lain, dan penghilangan konsonan terjadi di ketiga lagu. Perubahan perubahan fonetik ini dapat berkontribusi kepada diskusi mengenai hubungan lirik dan melodi pada lagu-lagu yang menggunakan dua atau lebih bahasa (bilingual).', 1745, 13, 4, 'Tidak', '2016-07-20'),
